<svelte:options tag="cooperation-button" />

<script lang='ts'>
    import { writable } from "svelte/store";
    import { serializeMsg, deserializeMsg, redbean_url } from "../../p2p_utils/communicator.js";

    export let type: "normal" | "vertical" = "normal";
    

    let dialogRef;
    let rootElement: HTMLElement;
    let text = writable("");
    let communicatorLoaded = false;

    const showPopup = async () => {
        dialogRef.showModal();
    };

    const closePopup = () => {
        dialogRef.close();
    };

    function sendPing() {
        let ip = $text.trim(); // Trim spaces
        ip = ip.replace(/\r/g, ""); // Remove carriage return if present
        console.log("IP Sent:", ip, "Length:", ip.length);

        // Validate IPv4 format (optional but recommended)
        const ipv4Regex = new RegExp(
            "^(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[1-9]?[0-9])\\." +
            "(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[1-9]?[0-9])\\." +
            "(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[1-9]?[0-9])\\." +
            "(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[1-9]?[0-9])$"
        );

        if (!ipv4Regex.test(ip)) {
            alert("Invalid IPv4 address.");
            return;
        }
        
        const msg = {
            sender_ip: "localhost",
            type: 3,
            data: ip
        };
        const serialized_msg = serializeMsg(msg);

        fetch(redbean_url, {
            method: "POST",
            headers: {
                "Content-type": "application/json; charset=UTF-8",
                "text": serialized_msg
            }
        });
    }
</script>

<main>
    <button 
        bind:this={rootElement}
        on:click={showPopup} 
        style="position: absolute; right: {type == 'vertical'
        ? `calc(var(--output-height) + min(1.5vw, 3vh))`
        : `min(0.75vw, 1.5vh)`}; top: min(5vw, 10vh); padding: min(0.25vw, 0.5vh); background-size: contain; width: min(2.5vw, 5.5vh); height: min(2.5vw, 5.5vh); display: flex; justify-content: center; align-items: center; z-index: 99; cursor: pointer; background-image: url('data:image/x-icon;base64,');"
    >
    </button>
    <dialog on:cancel={closePopup} bind:this={dialogRef}>
        <button class="close" on:click={closePopup}>x</button>
        <textarea bind:value={$text} placeholder="Enter IP"></textarea>
        <button on:click={sendPing}>PING</button>
    </dialog>
</main>